google_json\\examples\\android-proguard-.getSimpleTypeName:
{
    if (type == null) {
        return "null";
    }
    if (type instanceof Class) {
        return ((Class) type).getSimpleName();
    } else if (type instanceof ParameterizedType) {
        ParameterizedType pType = (ParameterizedType) type;
        StringBuilder sb = new StringBuilder(getSimpleTypeName(pType.getRawType()));
        sb.append('<');
        boolean first = true;
        for (Type argumentType : pType.getActualTypeArguments()) {
            if (first) {
                first = false;
            } else {
                sb.append(',');
            }
            sb.append(getSimpleTypeName(argumentType));
        }
        sb.append('>');
        return sb.toString();
    } else if (type instanceof WildcardType) {
        return "?";
    }
    return type.toString();
}

