    int length = s.length();
    for (int i = 0; i < length; i++) {
        char c = s.charAt(i);
        if (Character.isLetter(c)) {
            if (Character.isUpperCase(c)) {
                return s;
            }
            char uppercased = Character.toUpperCase(c);
            // For leading letter only need one substring
            if (i == 0) {
                return uppercased + s.substring(1);
