google_json\\metrics\\src\\main\\java\\c.readToken:
{
    while (true) {
        switch(reader.peek()) {
            case BEGIN_ARRAY:
                reader.beginArray();
                break;
            case END_ARRAY:
                reader.endArray();
                break;
            case BEGIN_OBJECT:
                reader.beginObject();
                break;
            case END_OBJECT:
                reader.endObject();
                break;
            case NAME:
                reader.nextName();
                break;
            case BOOLEAN:
                reader.nextBoolean();
                break;
            case NULL:
                reader.nextNull();
                break;
            case NUMBER:
                reader.nextLong();
                break;
            case STRING:
                reader.nextString();
                break;
            case END_DOCUMENT:
                return;
            default:
                throw new IllegalArgumentException("Unexpected token" + reader.peek());
        }
    }
}

